// File: src/components/Exam/ExamInterface.js
import React, { useEffect, useState } from 'react';
import api from '../../api/apiService';
import '../../App.css';

const ExamInterface = ({ exam, user, onComplete, onBack }) => {
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [timeLeft, setTimeLeft] = useState(exam.duration * 60);
  const [showAllQuestions, setShowAllQuestions] = useState(false);

  // Updated useEffect for timer (replace the existing timer useEffect)
useEffect(() => {
  // Timer countdown
  if (timeLeft > 0 && !submitting) {
    const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
    return () => clearTimeout(timer);
  } else if (timeLeft <= 0 && !submitting) {
    // Auto-submit when time is up
    handleSubmit();
  }
}, [timeLeft, submitting]);

// Updated handleSubmit function (replace the existing handleSubmit)
const handleSubmit = async () => {
  if (submitting) return; // Prevent double submission
  
  setSubmitting(true);
  
  try {
    const formattedAnswers = Object.entries(answers).map(([questionId, selectedOption]) => ({
      questionId,
      selectedOption
    }));

    console.log('Submitting answers:', { userId: user.id, examId: exam._id, answers: formattedAnswers });

    const result = await api.submitExam(user.id, exam._id, formattedAnswers);
    
    console.log('Received result:', result);
    
    if (result && result.result) {
      onComplete(result.result);
    } else {
      throw new Error('Invalid response format');
    }
  } catch (error) {
    console.error('Error submitting exam:', error);
    alert('Error submitting exam. Please check the console and try again.');
  } finally {
    setSubmitting(false);
  }
};

  const formatTime = (seconds) => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const getAnsweredCount = () => Object.keys(answers).length;

  if (loading) return <div className="loading">Loading exam questions...</div>;

  const currentQuestion = questions[currentQuestionIndex];

  return (
    <div className="exam-interface">
      <header className="exam-header">
        <div className="exam-info">
          <h2>{exam.title}</h2>
          <p>{exam.description}</p>
        </div>
        <div className="exam-controls">
          <div className="timer">
            <span className={timeLeft < 300 ? 'timer-warning' : ''}>Time Left: {formatTime(timeLeft)}</span>
          </div>
          <div className="progress">{getAnsweredCount()}/{questions.length} answered</div>
          <button onClick={onBack} className="btn-secondary">Back</button>
        </div>
      </header>

      <div className="exam-content">
        <div className="view-toggle">
          <button className={!showAllQuestions ? 'active' : ''} onClick={() => setShowAllQuestions(false)}>One by One</button>
          <button className={showAllQuestions ? 'active' : ''} onClick={() => setShowAllQuestions(true)}>All Questions</button>
        </div>

        {!showAllQuestions ? (
          <div className="single-question-view">
            <div className="question-navigation">
              <span>Question {currentQuestionIndex + 1} of {questions.length}</span>
              <div className="nav-buttons">
                <button onClick={() => setCurrentQuestionIndex(Math.max(0, currentQuestionIndex - 1))} disabled={currentQuestionIndex === 0} className="btn-secondary">Previous</button>
                <button onClick={() => setCurrentQuestionIndex(Math.min(questions.length - 1, currentQuestionIndex + 1))} disabled={currentQuestionIndex === questions.length - 1} className="btn-secondary">Next</button>
              </div>
            </div>

            <div className="question-card">
              <h3>{currentQuestion.questionText}</h3>
              <div className="options">
                {currentQuestion.options.map(option => (
                  <label key={option.optionKey} className="option-label">
                    <input type="radio" name={`question-${currentQuestion._id}`} value={option.optionKey} checked={answers[currentQuestion._id] === option.optionKey} onChange={() => handleAnswerSelect(currentQuestion._id, option.optionKey)} />
                    <span className="option-text">{option.optionKey}. {option.optionText}</span>
                  </label>
                ))}
              </div>
            </div>

            <div className="question-grid">
              {questions.map((_, index) => (
                <button key={index} className={`question-number ${index === currentQuestionIndex ? 'current' : ''} ${answers[questions[index]._id] ? 'answered' : ''}`} onClick={() => setCurrentQuestionIndex(index)}>{index + 1}</button>
              ))}
            </div>
          </div>
        ) : (
          <div className="all-questions-view">
            {questions.map((question, index) => (
              <div key={question._id} className="question-card">
                <h3>Question {index + 1}: {question.questionText}</h3>
                <div className="options">
                  {question.options.map(option => (
                    <label key={option.optionKey} className="option-label">
                      <input type="radio" name={`question-${question._id}`} value={option.optionKey} checked={answers[question._id] === option.optionKey} onChange={() => handleAnswerSelect(question._id, option.optionKey)} />
                      <span className="option-text">{option.optionKey}. {option.optionText}</span>
                    </label>
                  ))}
                </div>
              </div>
            ))}
          </div>
        )}

        <div className="submit-section">
          <div className="submit-info">
            <p>Make sure you have answered all questions before submitting.</p>
            <p>Answered: {getAnsweredCount()} out of {questions.length} questions</p>
          </div>
          <button onClick={handleSubmit} disabled={submitting || getAnsweredCount() === 0} className="btn-primary submit-btn">
            {submitting ? 'Submitting...' : 'Submit Exam'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExamInterface;
