import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import { useUser } from "../context/UserContext";

const Attempt = () => {
  const { examId } = useParams();
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const navigate = useNavigate();
  const { user, setScore } = useUser();

  useEffect(() => {
    axios.get(`http://localhost:5000/api/exams/${examId}/questions`).then((res) => {
      setQuestions(res.data);
    });
  }, [examId]);

  const handleChange = (questionId, option) => {
    setAnswers((prev) => ({ ...prev, [questionId]: option }));
  };

  const handleSubmit = async () => {
    const answerArray = questions.map((q) => ({
      question_id: q._id,
      selected_option: answers[q._id] || "",
    }));

    const res = await axios.post("http://localhost:5000/api/results/submit", {
      user_id: user._id,
      exam_id: examId,
      answers: answerArray,
    });

    setScore(res.data.score);
    navigate("/result");
  };

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h2 className="text-xl font-bold mb-4">Attempt Questions</h2>
      {questions.map((q, i) => (
        <div key={q._id} className="mb-6">
          <p className="font-semibold">Q{i + 1}: {q.question_text}</p>
          {q.options.map((opt, idx) => (
            <label key={idx} className="block">
              <input
                type="radio"
                name={q._id}
                value={opt}
                checked={answers[q._id] === opt}
                onChange={() => handleChange(q._id, opt)}
              /> {opt}
            </label>
          ))}
        </div>
      ))}
      <button onClick={handleSubmit} className="bg-green-600 text-white px-4 py-2 rounded">
        Submit Answers
      </button>
    </div>
  );
};

export default Attempt;
