// File: src/components/Exam/ResultView.js
import React from 'react';
import '../../App.css';

// Result View Component
const ResultView = ({ result, examTitle, onBack }) => {
  if (!result) {
    return <div className="loading">Loading results...</div>;
  }

  return (
    <div className="result-view">
      <div className="result-container">
        <div className="result-header">
          <h2>Exam Results</h2>
          <button onClick={onBack} className="btn-secondary">Back to Dashboard</button>
        </div>

        <div className="result-summary">
          <div className="exam-title">
            <h3>{examTitle || 'Exam Complete'}</h3>
          </div>
          <div className="score-display">
            <div className="score-circle">
              <span className="score-text">{result.percentage}%</span>
            </div>
            <div className="score-details">
              <h3>Your Score</h3>
              <p>{result.score} out of {result.totalQuestions} questions correct</p>
              <div className={`result-status ${result.percentage >= 70 ? 'passed' : 'failed'}`}>
                {result.percentage >= 70 ? '✓ Passed' : '✗ Failed'}
              </div>
            </div>
          </div>
        </div>

        <div className="answer-review">
          <h3>Answer Review</h3>
          <div className="answers-grid">
            {result.answers && result.answers.map((answer, index) => (
              <div key={index} className={`answer-card ${answer.isCorrect ? 'correct' : 'incorrect'}`}>
                <div className="question-number">Question {index + 1}</div>
                <div className="answer-result">
                  <span className="your-answer">
                    Your Answer: {answer.selectedOption}
                  </span>
                  <span className="correct-answer">
                    Correct Answer: {answer.correctOption}
                  </span>
                  <span className={`result-icon ${answer.isCorrect ? 'correct' : 'incorrect'}`}>
                    {answer.isCorrect ? '✓' : '✗'}
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};
export default ResultView;
