import React, { useState, useEffect } from 'react';
import api from '../services/api';

const ExamList = ({ onExamSelect, }) => {
  const [exams, setExams] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
  const fetchExams = async () => {
    try {
      const examData = await api.getExams();
      console.log("Fetched exams:", examData);
      setExams(examData);
    } catch (error) {
      console.error('Error fetching exams:', error);
    }
    setLoading(false);
  };

  fetchExams();
}, []);


  const handleExamClick = async (exam) => {
    try {
      const examDetails = await api.getExamById(exam._id);
      onExamSelect(examDetails);
    } catch (error) {
      console.error('Error fetching exam details:', error);
    }
  };

  if (loading) {
    return <div className="loading">Loading exam papers...</div>;
  }

  return (
    <div className="exam-list-container">
      <h2>Available Mock Exam Papers</h2>
      

      <div className="exam-cards">
        {exams.map((exam) => (
          <div key={exam._id} className="exam-card">
            <h3>{exam.title}</h3>
            <p>{exam.description}</p>
            <div className="exam-info">
              <span>üìù 5 Questions</span>
            </div>
            <button 
              onClick={() => handleExamClick(exam)}
              className="start-exam-btn"
            >
              Start Exam
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ExamList;
