import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import api from '../../api/apiService';  // Your existing api service
import ResultView from '../Exam/ResultView'; // Adjust path if needed

const Result = () => {
  const { resultId } = useParams();
  const navigate = useNavigate();

  const [result, setResult] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchResultDetails = async () => {
      try {
        const data = await api.getResultDetails(resultId);
        setResult(data.result);
        setAnswers(data.answers);
      } catch (error) {
        console.error('Failed to load result details:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchResultDetails();
  }, [resultId]);

  if (loading) return <div className="loading">Loading result...</div>;

  return (
    <ResultView
      result={result}
      answers={answers}
      onBack={() => navigate('/dashboard')} // Adjust route as needed
    />
  );
};

export default Result;
