import React, { useState } from 'react';
import api from '../../api/apiService';
import '../../App.css';

const Register = ({ onRegister, onSwitchToLogin }) => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    setLoading(true);
    try {
      const result = await api.register(name, email, password);
      if (result.user) {
        onRegister(result.user);
      } else {
        setError(result.message || 'Registration failed');
      }
    } catch {
      setError('Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-form">
        <h2>Register for MCQ System</h2>
        <form onSubmit={handleSubmit}>
          {/* Name */}
          <div className="form-group">
            <label>Name:</label>
            <input value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
          {/* Email */}
          <div className="form-group">
            <label>Email:</label>
            <input value={email} type="email" onChange={(e) => setEmail(e.target.value)} required />
          </div>
          {/* Password */}
          <div className="form-group">
            <label>Password:</label>
            <input value={password} type="password" onChange={(e) => setPassword(e.target.value)} required />
          </div>
          {/* Confirm Password */}
          <div className="form-group">
            <label>Confirm Password:</label>
            <input value={confirmPassword} type="password" onChange={(e) => setConfirmPassword(e.target.value)} required />
          </div>
          {error && <div className="error-message">{error}</div>}
          <button className="btn-primary" disabled={loading}>{loading ? 'Registering...' : 'Register'}</button>
        </form>
        <p className="auth-switch">
          Already have an account?
          <button className="link-button" onClick={onSwitchToLogin}>Login here</button>
        </p>
      </div>
    </div>
  );
};

export default Register;
