import React from 'react';
import '../../App.css';

const ResultView = ({ result, exam, questions, onBack }) => {
  if (!result || !exam || !questions || !Array.isArray(questions)) {
    return <p>Loading result details...</p>;
  }

  const getQuestionById = (id) => questions.find((q) => q._id === id);

  const formatDate = (dateStr) => {
    if (!dateStr) return 'Date not available';
    const date = new Date(dateStr);
    return date.toLocaleString();
  };

  const getStatus = (selected, correct) => {
    return selected === correct ? '✓ Correct' : '✗ Incorrect';
  };

  return (
    <div className="result-view">
      <header className="result-header">
        <h2>{exam?.title || 'Exam Title Not Available'}</h2>
        <p>{exam?.description || 'No description available.'}</p>
        <p><strong>Completed on:</strong> {formatDate(result?.submittedAt)}</p>
        <button onClick={onBack} className="btn-secondary">Back to Results</button>
      </header>

      <section className="result-summary">
        <h3>Score:</h3>
        <p>{result?.score} / {questions.length}</p>
        <p><strong>Percentage:</strong> {result?.percentage}%</p>
        <p><strong>Status:</strong> {result?.passed ? 'Passed' : 'Failed'}</p>
      </section>

      <section className="question-review">
        <h3>Question Review</h3>
        {result?.answers.map((answer, index) => {
          const question = getQuestionById(answer.questionId);
          const correctOption = question?.options.find(opt => opt.optionKey === question.correctOption);
          const selectedOption = question?.options.find(opt => opt.optionKey === answer.selectedOption);

          return (
            <div key={answer.questionId} className="question-review-card">
              <p><strong>Q{index + 1}:</strong> {question?.questionText || 'Question data not available'}</p>
              <p><strong>Selected:</strong> {selectedOption ? `${selectedOption.optionKey}. ${selectedOption.optionText}` : answer.selectedOption}</p>
              <p><strong>Correct:</strong> {correctOption ? `${correctOption.optionKey}. ${correctOption.optionText}` : question?.correctOption}</p>
              <p><strong>Status:</strong> {getStatus(answer.selectedOption, question?.correctOption)}</p>
            </div>
          );
        })}
      </section>
    </div>
  );
};

export default ResultView;
