// File: src/components/Exam/ResultView.js
import React from 'react';
import '../../App.css';

const ResultView = ({ result, onBack }) => {
  if (!result) {
    return <div className="loading">Loading result...</div>;
  }

  // Safely access exam data
  const exam = result.examId || {}; // fallback to empty object if undefined
  const answers = Array.isArray(result.answers) ? result.answers : [];

  return (
    <div className="result-view">
      <header className="result-header">
        <h2>Exam Result</h2>
        <button onClick={onBack} className="btn-secondary">Back to Dashboard</button>
      </header>

      <div className="result-summary">
        <h3>{exam.title || 'Exam Title Not Available'}</h3>
        <p className="result-description">{exam.description || 'No description available.'}</p>
        <div className="result-details">
          <div className="result-score">
            <strong>Score:</strong> {result.score ?? 'N/A'} / {result.totalQuestions ?? 'N/A'}
          </div>
          <div className="result-percentage">
            <strong>Percentage:</strong> {result.percentage ?? 'N/A'}%
          </div>
          <div className={`result-status ${(result.percentage ?? 0) >= 70 ? 'passed' : 'failed'}`}>
            {(result.percentage ?? 0) >= 70 ? 'Passed' : 'Failed'}
          </div>
          <div className="result-date">
            <strong>Completed on:</strong>{' '}
            {result.completedAt ? new Date(result.completedAt).toLocaleString() : 'Date not available'}
          </div>
        </div>
      </div>

      <div className="result-answers">
        <h4>Question Review</h4>
        {answers.length === 0 && <p>No answers to display.</p>}
        {answers.map((answer, index) => {
          const question = answer.questionId || {};
          const options = Array.isArray(question.options) ? question.options : [];

          return (
            <div key={question._id || index} className="question-review">
              <h5>Q{index + 1}: {question.questionText || 'Question text not available'}</h5>
              <div className="answer-options">
                {options.map(option => {
                  const isSelected = answer.selectedOption === option.optionKey;
                  const isCorrect = answer.correctAnswer === option.optionKey;
                  const isWrong = isSelected && !isCorrect;
                  return (
                    <div
                      key={option.optionKey}
                      className={`option-result ${isCorrect ? 'correct' : ''} ${isWrong ? 'wrong' : ''}`}
                    >
                      {option.optionKey}. {option.optionText}
                    </div>
                  );
                })}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ResultView;
